/*
 * Module dependencies
 */

var fs = require('fs');
var path = require('path');

/*
 * Variables
 */

var cache = {};
var depsRoot = path.resolve('node_modules');

/*
 * Expose
 */

module.exports = getSupport;

/*
 * getSupport
 */

function getSupport (name, root) {
  var dep = cache[name];
  if (dep || false === dep) return dep;
  var file = path.join(root || depsRoot, name);
  if (fs.existsSync(file)) dep = require(file);
  dep = cache[name] = dep || !!dep;
  return dep;
}
